static s32 spi_write_data(const unsigned char *buf, unsigned int len)
{
    u32 count = 0;
    u16 data;

     if((NULL == buf)||(0 == len))
    {
        return LCD_ERROR;
    }

    for(count = 0;(unsigned int)count< len; count++)
    {
        data= (u16)(buf[count] | (1<<8)); /* 数据 */
        /*cprintf("\r\ndata= %x",data);*/
        spi_send(SPI_NO_LCD,SPI_CS_LCD,&data,1);

    }
    return LCD_OK;
}

static s32 spi_write_cmd(const unsigned char *buf, unsigned int len)
{
    u32 count = 0;
    u16 cmd;

    if((NULL == buf)||(0 == len))
    {
        return LCD_ERROR;
    }

    for(count = 0;(unsigned int)count< len; count++)
    {
        cmd = ((unsigned short)buf[count]) & ~(1 << 8); /* 命令 */
        /* cprintf("\r\ncmd= %x",cmd);*/
        spi_send(SPI_NO_LCD,SPI_CS_LCD,&cmd,1);


    }
    return LCD_OK;

}/*lint !e550*/


static s32 balong_lcd_toggle(struct balong_lcd_seq *seq, int sz)
{
    u8 buf[1];
    u32 ret = 0;
    int count;
    for (count = 0; count < sz; count++)
    {
        /*cprintf("balong_lcd_toggle!\n");*/
        buf[0] = seq[count].value;
        if(seq[count].format == 'c')
        {
            ret = spi_write_cmd(buf, 1);  /* command */
            if(ret)
            {
                return ret;
            }

        }
        else if(seq[count].format == 'd')
        {
            ret = spi_write_data(buf, 1); /* data */
            if(ret)
            {
                return ret;
            }

        }
        else if(seq[count].format=='w')
        {
            mdelay((seq[count].value)); /* delay */
        }
    }
    return LCD_OK;
}


static s32 balong_lcd_window_set(u16 x0,u16 y0,u16 x1,u16 y1)
{

    s32 ret = 0;
    u8 buf[1];
    if((x0>LCD_WIDTH)|(x1>LCD_WIDTH)|(y0>LCD_HIGHT)|(y1>LCD_HIGHT))
    {
        return LCD_ERROR;
    }

    buf[0] = COLUMN_ADDRESS_SET;
    ret = spi_write_cmd(buf, 1);
    if(ret)
    {
        return ret;
    }

    buf[0] = (u8)(x0 >> 8);
    ret = spi_write_data(buf, 1); /* data */
    if(ret)
    {
        return ret;
    }
    buf[0] = (u8)x0;
    ret = spi_write_data(buf, 1); /* data */
    if(ret)
    {
        return ret;
    }
    buf[0] = (u8)(x1 >> 8);
    ret = spi_write_data(buf, 1); /* data */
    if(ret)
    {
        return ret;
    }
    buf[0] = (u8)x1;
    ret = spi_write_data(buf, 1); /* data */
    if(ret)
    {
        return ret;
    }


    buf[0] = PAGE_ADDRESS_SET;
    ret = spi_write_cmd(buf, 1);
    if(ret)
    {
        return ret;
    }

    buf[0] = (u8)(y0 >> 8);
    ret = spi_write_data(buf, 1); /* data */
    if(ret)
    {
        return ret;
    }
    buf[0] = (u8)y0;
    ret = spi_write_data(buf, 1); /* data */
    if(ret)
    {
        return ret;
    }
    buf[0] = (u8)(y1 >> 8);
    ret = spi_write_data(buf, 1); /* data */
    if(ret)
    {
        return ret;
    }
    buf[0] = (u8)y1;
    ret = spi_write_data(buf, 1); /* data */
    if(ret)
    {
        return ret;
    }

    buf[0] = MEMORY_WRITE;
    ret = spi_write_cmd(buf, 1);
    if(ret)
    {
        return ret;
    }

   return LCD_OK;
}

