/*****************************************************************************
* 函 数 名  : lcd_reset
* 功能描述  : 复位解复位 LCD
* 输入参数  :
* 输出参数  : 无
* 返 回 值  :
*****************************************************************************/
void lcd_reset(void)
{
    set_hi_sc_ctrl3_lcd_rst_n(1);
    mdelay(10);
    set_hi_sc_ctrl3_lcd_rst_n(0);
    mdelay(10);
    set_hi_sc_ctrl3_lcd_rst_n(1);
    mdelay(50);
}
s32 balong_lcd_display_allblack(void)
{

    u32 count;
    s32 ret;
    u8 data[1];
    u32 len = (LCD_WIDTH+1)*(LCD_HIGHT+1)*2; /* 565数据格式 */
    ret = balong_lcd_window_set(0,0,LCD_WIDTH,LCD_HIGHT);

    if(ret)
    {
        return ret;
    }

    for(count=0;(u32)count<len;count++)
    {

        data[0] = 0x0;     /* black rgb 0 */
        ret = spi_write_data(data,1);
        if(ret)
        {
            return ret;
        }
    }


    return LCD_OK;
}

s32 lcd_refresh_bybit(u16 x0,u16 y0,u16 x1,u16 y1,const u8 * pbuffer)
{

    s32 ret = 0;
    u32 bitNum = 0;
    u16 xloop,yloop;
    u8 white[2] = {0XFF,0xFF};
    u8 black[2] = {0X00,0X00};
    u8 *buffer = pbuffer;
    if(NULL == pbuffer)
    {
        return LCD_ERROR;
    }

    ret = balong_lcd_window_set(x0, y0, x1, y1);
    if(ret)
    {

        return ret;
    }
    for (yloop = y0; yloop <= y1; yloop++)
    {
        for (xloop = x0; xloop <= x1; xloop++)
        {
            if( bitNum ==8 )
            {
                buffer++;
                bitNum=0;    /*一个字节输出完毕时指针后移，位标志清零*/
            }

            if( (((*buffer)>>(7-bitNum)) & 0x01) == 0x01)    /*从高位开始判断*/
            {
                ret = spi_write_data(white,2);
                if(ret)
                {
                    return ret;
                }

            }
            else
            {
                ret = spi_write_data(black,2);
                if(ret)
                {
                    return ret;
                }

             }
            bitNum++;
        }
    }
    return LCD_OK;
}
void lcd_char_display(u16 ucX, u16 ucY, u8 *pucChar)
{
    u8 *pucStr = (void*)0;


    if ((*pucChar >= 'A' )&& (*pucChar <= 'Z' ))
    {
        pucStr = (unsigned char *)(g_aucStr_A_Z_8x16 + ((*pucChar -'A')*16));
    }
    else if ((*pucChar >= 'a' ) && (*pucChar <= 'z'))
    {
        pucStr = (unsigned char *)(g_aucStr_a_z_8x16 + ((*pucChar-'a')*16));
    }
    else if ((*pucChar >= '0' )&& (*pucChar <= '9'))
    {
        pucStr = (unsigned char *)(g_aucNum_0_9_8x16 + ((*pucChar-'0')*16));
    }
    else if (*pucChar == ' ')
    {
        pucStr = (unsigned char *) g_aucSpace;
    }
    else if (*pucChar == '!')
    {
        pucStr = (unsigned char *)g_aucExclamatory;
    }

    else if (*pucChar == '-')
    {
        pucStr = (unsigned char *)g_aucDash;
    }

    else
    {
    }
    (void)lcd_refresh_bybit(ucX, ucY, ucX+CHAR_WIDTH - 1, ucY+CHAR_HIGHT- 1, pucStr);

    return;
}


void lcd_string_display(u16 ucX, u16 ucY, char *pucStr)
{
    while ('\0' != *pucStr)
    {
        lcd_char_display(ucX, ucY, pucStr++);
        ucX += CHAR_WIDTH;
        if (ucX > LCD_WIDTH)
        {
            ucX = 0;
            ucY += CHAR_HIGHT;
        }
    }

}

void lcd_show_string(char* str)
{
    int Xstart = 0;

    if( NULL == str )
    {
        cprintf("[LCD] lcd_show_string, but str is NULL!\n");
        return;
    }

    Xstart = ((LCD_WIDTH+1) - strlen(str)*8) / 2;
    if( 0 > Xstart )
    {
        Xstart = 0;
    }

    lcd_string_display( Xstart, (LCD_HIGHT+1)/2 - 6, str );
}

int lcd_read_flash(int offset)
{
    int ret = -1;
    int i = 0;
    int blackNUM = 0;
    int whiteNUM = 0;

    memset(gImage_flash, 0, INIT_LOGO_SIZE);
    ret = bsp_nand_read(PTABLE_OEMINFO_NM, offset, gImage_flash,INIT_LOGO_SIZE ,NULL);
    if( NAND_ERROR == ret )
    {
        cprintf("[LCD] Reading image file, but flash_read return error,ret=%d!\n",ret);
        return LCD_ERROR;
    }

    /*图片正确性验证,全白和全黑的情况属于异常情况之一*/
    for(i = 0; i< INIT_LOGO_SIZE; i++)
    {
        if( 0x00 == gImage_flash[i] )
        {
            whiteNUM++;
        }
        if( 0xFF == gImage_flash[i] )
        {
            blackNUM++;
        }
    }

    /*考虑到flash读取数据时万一个别数据读取不对，发生纯黑图中偶有几个白点，
      或者纯白图中偶有几个黑点的情况也属异常，特减去了5个像素以防万一*/
    if(((INIT_LOGO_SIZE - 10) <= whiteNUM) || ((INIT_LOGO_SIZE -10) <= blackNUM))
    {
        cprintf("[LCD] Reading image file, but it is a white or black image in flash!\n");
        return LCD_ERROR;
    }

    return LCD_OK;
}
s32 lcd_warm_up(void)
{
    s32 ret = 0;

    /*上电,此处注意不同的lcd屏，上电的顺序可能不同*/
    bsp_pmu_lcd_io_power_switch(POWER_ON);

#ifdef CONFIG_FB_2_4_INCH_BALONG
    bsp_pmu_lcd_analog_volt_set(2850000);/*设置为2.85v*/
#elif defined CONFIG_FB_1_4_5_INCH_BALONG
    bsp_pmu_lcd_analog_volt_set(2600000);/*设置为2.6v*/
#endif
    bsp_pmu_lcd_analog_power_switch(POWER_ON);

    mdelay(2);/*delay，等待上电稳定再进行其他操作*/

    /*初始化spi*/
    ret = spi_init(SPI_NO_LCD);
    if(ret)
    {
        return ret;
    }
    /*复位解复位lcd*/
    lcd_reset();
    /*初始化lcd*/
    lcd_init();

    ret = balong_lcd_display_allblack();
    if(ret)
    {
        return ret;
    }
    mdelay(100);
    return LCD_OK;

}

s32 lcd_refresh(u16 x0,u16 y0,u16 x1,u16 y1,const u8 * buf)
{
    s32 ret = 0;
    u32 len = (y1 - y0 + 1)*(x1 - x0 + 1) * BPP;
    /* set window before write image */
    ret = balong_lcd_window_set(x0, y0, x1, y1);

    if(ret){
        cprintf("lcd window set error\n");
        return ret;
    }

    ret = spi_write_data(buf,len);
    if(ret){
        cprintf("lcd refresh error\n");
        return ret;
    }

    return LCD_OK;
}


s32 lcd_init_image(int offset)
{
   int ret = 0;
    gImage_flash = alloc(INIT_LOGO_SIZE);
    if( NULL == gImage_flash )
    {
        cprintf("lcd alloc ERROR!\n");
        ret = LCD_ERROR;
    }
    else
    {
        ret = lcd_read_flash(offset);
    }

    if(LCD_OK == ret)
    {

        ret = lcd_refresh(0, 0,  LCD_WIDTH, LCD_HIGHT, (unsigned char *)gImage_flash);
        if(ret)
        {
            cprintf("lcd_init_image from flash error!\n");
            return ret;
        }
    }
    else
    {

        ret = lcd_refresh_bybit(LCD_INIT_X0, LCD_INIT_Y0,  LCD_INIT_X1, LCD_INIT_Y1, (unsigned char *)gImage_init);
        if(ret)
            {
                cprintf("lcd_init_image from fastboot error!\n");
                return ret;
            }
    }

    return LCD_OK;
}
/*****************************************************************************
* 函 数 名  : LcdPowerOn
*
* 功能描述  : LCD加电启动并初始化
*
* 输入参数  : 无
*
* 输出参数  : 无
*
* 返 回 值  : 初始化成功或者失败
*****************************************************************************/
s32 lcd_power_on(void)
{
    u32 product_type = 0xff;
    (void)lcd_init_image(INIT_LOGO_OFFSET);  /*显示初始化logo*/

    product_type = bsp_version_get_board_chip_type();


    if((HW_VER_PRODUCT_UDP == product_type)||(HW_VER_PRODUCT_E5379 == product_type))
      BACKLIGHT = GPIO_2_31;

    else
        BACKLIGHT = GPIO_0_12;

    mdelay(50); /*屏幕反应不过来加个延时*/
    #ifdef CONFIG_FB_1_4_5_INCH_BALONG
    pmu_dr1_current_set(18);    /*设置DR1控制寄存器 18mA*/
    pmu_dr1_on();/*打开DR1背光*/
    #elif defined CONFIG_FB_2_4_INCH_BALONG
    gpio_direction_output(BACKLIGHT,1);
    #endif

    return LCD_OK;
}



